REPLACE INTO "events" VALUES (63,'conference_major','2012-08-06','2012-08-10',NULL,'Arctic Schools Network - Common Watersheds',NULL,'This conference invites some specific schools within the Life-Link network of schools, and also schools within the Arctic Region states, to discuss how schools can contribute to confidence building and sustainable living around common Water regions / Watersheds.\n\nMore information at http://conference.life-link.org\n\nIf you have photos, send them to <a href=\"mailto:conference@life-link.org\">conference@life-link.org</a> and we will share them with the rest of the world!','sets/72157630013167657',NULL,752,'yes');
